// screens/ProfileScreen.js
import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';

export default function ProfileScreen() {
  const [historyDisplay, setHistoryDisplay] = useState(false);
  const [history, setHistory] = useState([
    // Sample history data
    { year: 2024, month: 7, day: 28, hour: 14, minute: 30, duration: '30 minutes' },
    { year: 2024, month: 7, day: 27, hour: 12, minute: 0, duration: '20 minutes' },
  ]);

  return (
    <View style={styles.container}>
      <Text style={styles.text}>Profile Screen</Text>
      <View style={styles.history}>
        <TouchableOpacity onPress={() => setHistoryDisplay(!historyDisplay)}>
          <Text style={styles.historyTitle}>Meditation History</Text>
        </TouchableOpacity>

        {historyDisplay && (
          <ScrollView style={styles.historyContainer}>
            {history.map((entry, index) => (
              <View key={index} style={styles.historyItem}>
                <Text style={styles.historyText}>
                  {`${entry.year}-${entry.month}-${entry.day} ${entry.hour}:${entry.minute} - Duration: ${entry.duration}`}
                </Text>
              </View>
            ))}
          </ScrollView>
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  text: {
    fontSize: 24,
    marginBottom: 20,
  },
  history: {
    flex: 1,
    padding: 10,
    width: '100%',
    alignItems: 'center',
  },
  historyTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  historyContainer: {
    width: '100%',
    padding: 10,
    borderColor: 'black',
    borderWidth: 2,
    borderRadius: 10,
    backgroundColor: 'rgba(0, 0, 0, 0.1)',
  },
  historyItem: {
    padding: 10,
    marginBottom: 5,
    borderBottomColor: 'black',
    borderBottomWidth: 1,
  },
  historyText: {
    fontSize: 18,
  },
});
