import { StatusBar } from 'expo-status-bar';
import { useReducer, useState } from 'react';
import { Button, StyleSheet, View } from 'react-native';
import { useRef } from 'react';

const MED_TIME = 10;
const REST_TIME = 2;

export default function App() {
  const [started, setStarted] = useState(false);
  const [paused, setPaused] = useState(false);
  const [count, setCount] = useState(MED_TIME + REST_TIME);
  const ref = useRef(null);

  const reset = () => {
    if (ref.current) {
      clearInterval(ref.current);
      ref.current = null;
    }
    setStarted(false);
    setPaused(false);
  }

  const startTimer = () => {
    ref.current = setInterval(() => {
      setCount(prev => {
        if (prev > 0) {
          return prev - 1;
        }
        reset();
        return MED_TIME + REST_TIME
      })

    }, 1000)
  }

  const start = () => {
    setStarted(true);
    startTimer();

  }
  const stop = () => {
    reset();
    setCount(MED_TIME + REST_TIME);
  }
  const pause = () => {
    setPaused(true);
    clearInterval(ref.current);

  }
  const resume = () => {
    setPaused(false)
    startTimer;

  }
  return (
    <View style={styles.container}>
      <p> {count}</p>
      {started ? <Button title='Stop' onPress={stop} /> : <Button title="Start" onPress={start} />}
      {started && !paused ? <Button title='Pause' onPress={pause} /> : null}
      {started && paused ? <Button title='Resume' onPress={resume} /> : null}

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
