import { useState } from 'react';
import { Button, StyleSheet, View, Image } from 'react-native';
import { useRef } from 'react';

const MED_TIME = 3;
const REST_TIME = 2;

export default function App() {
  const [started, setStarted] = useState(false);
  const [paused, setPaused] = useState(false);
  const [count, setCount] = useState(MED_TIME + REST_TIME);
  const [color, setColor] = useState('white')
  const ref = useRef(null);


  const clearTimer = () => {
    if (ref.current) {
      clearInterval(ref.current);
      ref.current = null;
    }
  }
  const reset = () => {
    clearTimer();
    setColor('white');
    setStarted(false);
    setPaused(false);
  }

  const startTimer = () => {
    clearTimer();
    ref.current = setInterval(() => {
      setCount(prev => {
        if (prev > REST_TIME) {
          setColor('green');
        }
        if (prev == REST_TIME) {
          setColor('orange');
        }
        if (prev > 0) {
          return prev - 1;
        }
        reset();
        return MED_TIME + REST_TIME
      })

    }, 1000)
  }

  const start = () => {
    setStarted(true);
    setColor("green");
    startTimer();

  }
  const stop = () => {
    reset();
    setCount(MED_TIME + REST_TIME);
  }
  const pause = () => {
    clearTimer();
    setPaused(!paused);

  }
  const resume = () => {
    startTimer();
    setPaused(!paused);

  }
  return (
    <View style={[styles.container, { backgroundColor: color }]}>

      <Image source={require('./media/meditation.jpg')}
        style={{ width: 200, height: 200 }} />
      <p> {count}</p>
      <View style={styles.inputContainer}>

        <View style={styles.medInput}>asdas</View>
        <View style={styles.restInput}>asdasd</View>

      </View>
      {started ? <Button title='Stop' onPress={stop} /> : <Button title="Start" onPress={start} />}
      {started && !paused ? <Button title='Pause' onPress={pause} /> : null}
      {started && paused ? <Button title='Resume' onPress={resume} /> : null}

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputContainer: {
    flex: 0.5,
    flexDirection: 'row'
  }
});
