import { useState, useEffect } from 'react';
import { Button, StyleSheet, View, Image, TextInput } from 'react-native';
import { useRef } from 'react';

export default function App() {
  const [started, setStarted] = useState(false);
  const [paused, setPaused] = useState(false);
  const [time, setTime] = useState({ med_time: 3, rest_time: 2 });

  const [color, setColor] = useState('white');
  const ref = useRef(null);


  const initialCount = time.med_time + time.rest_time;
  const [count, setCount] = useState(initialCount);

  const clearTimer = () => {
    if (ref.current) {
      clearInterval(ref.current);
      ref.current = null;
    }
  }

  const reset = () => {
    clearTimer();
    setColor('white');
    setStarted(false);
    setPaused(false);
  }

  const startTimer = () => {
    clearTimer();
    ref.current = setInterval(() => {
      setCount(prev => {
        if (prev > time.rest_time) {
          setColor('green');
        }
        if (prev === time.rest_time) {
          setColor('orange');
        }
        if (prev > 0) {
          return prev - 1;
        }
        reset();
        return time.med_time + time.rest_time;
      });
    }, 1000)
  }

  const start = () => {
    setStarted(true);
    setColor("green");
    startTimer();
  }

  const stop = () => {
    reset();
    setCount(initialCount);
  }

  const pause = () => {
    clearTimer();
    setPaused(!paused);
  }

  const resume = () => {
    startTimer();
    setPaused(!paused);
  }

  const textChange = (field, text) => {
    const newValue = parseInt(text) || 0;
    setTime({ ...time, [field]: newValue });
  }

  useEffect(() => {

    const newCount = time.med_time + time.rest_time;
    setCount(newCount);
  }, [time]);

  return (
    <View style={[styles.container, { backgroundColor: color }]}>
      <Image source={require('./media/meditation.jpg')} style={{ width: 200, height: 200 }} />
      <p> {count}</p>
      <View style={styles.inputContainer}>
        <View style={styles.textInput}>
          <TextInput
            placeholder='Meditation Time'
            onChangeText={(text) => textChange('med_time', text)}
          />
        </View>
        <View style={styles.textInput}>
          <TextInput
            placeholder='Rest Time'
            onChangeText={(text) => textChange('rest_time', text)}
          />
        </View>
      </View>
      {started ? <Button title='Stop' onPress={stop} /> : <Button title="Start" onPress={start} />}
      {started && !paused ? <Button title='Pause' onPress={pause} /> : null}
      {started && paused ? <Button title='Resume' onPress={resume} /> : null}
      <View style={styles.history}></View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputContainer: {
    flex: 0.5,
    flexDirection: 'row',
    padding: 2,
  },
  textInput: {
    borderColor: 'gray',
    borderWidth: 2,
    borderRadius: 10,
    padding: 2,
    margin: 5,
    height: 25
  },
  history: {
    flex: 1,
    padding: 5
  }
});
